let themeToggler=document.getElementById("theme-toggle");const toggle=document.getElementById("toggle"),body=document.body;themeToggler.onclick=()=>{"dark"==document.body.classList?(toggle.checked=!0,document.body.classList.remove("dark"),document.body.classList.add("light")):"light"==document.body.classList&&(toggle.checked=!1,document.body.classList.remove("light"),document.body.classList.add("dark"))},toggle.addEventListener("input",e=>{const isChecked=e.target.checked;isChecked?(body.classList.remove("dark"),body.classList.add("light")):(body.classList.remove("light"),body.classList.add("dark"))});